import React from 'react';
import defaultAvatar from '../assets/profileImage/avatar-1.jpg'; // Import the default avatar image

function Profile({ avatar, userName, bio }) {
  const profileAvatar = avatar || defaultAvatar; // Use the provided avatar, or the default if none is provided

  return (
    <div className="profile">
      <div className="profile-info">
        <div className="profile-picture">
          <img src={profileAvatar} className="profile-avatar" alt="avatar" />
        </div>
        <p>
          <strong>@</strong> {userName}
        </p>
        <p>
          <strong>Bio</strong> {bio}
        </p>
      </div>
    </div>
  );
}

export default Profile;
