import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios'; // Import axios for making API requests
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

// Create a context for user data
const UserContext = createContext();

export function UserContextProvider({ children }) {
  const navigate = useNavigate(); // Get the navigate function
  const [user, setUser] = useState(null);

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('https://thre-points.herokuapp.com/api/login', {
        username: email,
        password: password,
      });

      if (response.status === 200) {
        const data = response.data;
        localStorage.setItem('token', data.token);
        setAuthenticated(true);
        navigate('/profile'); // Use navigate to redirect to the profile page
      } else {
        // Handle login error
        // You can set an error state here
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLogout = () => {
    // Clear localStorage and reset authentication state
    localStorage.removeItem('token');
    setAuthenticated(false);
    setCurrentUser(null);
    navigate('/login'); // Use navigate to redirect to the login page
  };

  // Use an effect to fetch user data when the component mounts
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // If a token exists in localStorage, fetch user data
      axios
        .get('https://thre-points.herokuapp.com/api/users/profile', {
          headers: {
            Authorization: `Bearer ${token}`, // Use the token from localStorage
          },
        })
        .then((response) => {
          if (response.status === 200) {
            const userData = response.data;
            setUser(userData);
          } else {
            // Handle the case when the request is not successful, e.g., token is invalid
            // Clear localStorage and reset user state
            localStorage.removeItem('token');
            setUser(null);
            navigate('/login'); // Redirect to the login page
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  }, [navigate]);

  if (!user) {
    return (
      <div className="p-5 text-center">
        <div className="spinner-border text-primary" role="status" />
      </div>
    );
  }

  return (
    // Provide user data through the context
    <UserContext.Provider value={{ user }}>
      {children}
    </UserContext.Provider>
  );
}

export function useUserContext() {
  return useContext(UserContext);
}

export default UserContextProvider;
