import axios from "axios";

// Set the base URL for API requests
const API_BASE_URL = "https://thre-points.herokuapp.com/api";

// Function to send a POST request for login
export function login(username, password) {
  return axios
    .post(`${API_BASE_URL}/login`, {
      username,
      password,
    })
    .then((response) => {
      // Assuming the response contains a token
      const token = response.data.token;

      // Do something with the token, such as storing it in local storage
      localStorage.setItem("token", token);

      // Return the token or other relevant data
      return token;
    });
}

// Function to get the list of posts
export function getPostList() {
  return axios
    .get("http://localhost:3000/postList", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
    .then((response) => response.data);
}

// Function to get the user profile
export function getProfile() {
  return axios
    .get("http://localhost:3000/profile", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
    .then((response) => response.data);
}
