import React, { useState, useEffect } from 'react'; // Import useEffect
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/NavBar';
import PostList from './components/PostList';
import Profile from './components/Profile';
import SearchBar from './components/SearchBar';
import Login from './components/Login';
import axios from 'axios';
import { UserContextProvider, useUserContext } from './contexts/user-context';


function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const navigate = useNavigate();
  const { user, setAuthenticated, setCurrentUser, setUser } = useUserContext(); // Destructure context values

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('https://thre-points.herokuapp.com/api/login', {
        username: email,
        password: password,
      });

      if (response.status === 200) {
        const data = response.data;
        localStorage.setItem('token', data.token);
        setAuthenticated(true);
        navigate('/profile');
      } else {
        // Handle login error
        // You can set an error state here
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setAuthenticated(false);
    setCurrentUser(null);
    navigate('/login');
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('https://thre-points.herokuapp.com/api/users/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          if (response.status === 200) {
            const userData = response.data;
            setUser(userData);
          } else {
            localStorage.removeItem('token');
            setUser(null);
            setAuthenticated(false);
            navigate('/login');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  }, [navigate, setAuthenticated, setCurrentUser, setUser]); // Include context dependencies

  if (!user) {
    return (
      <div className="p-5 text-center">
        <div className="spinner-border text-primary" role="status" />
      </div>
    );
  }

  return (
    <Router>
      <PhoneFrame>
        <div className="container">
          <NavBar
            onLogoClick={() => navigate('/PostList')}
            onLogout={handleLogout}
            authenticated={user !== null}
          />
          <SearchBar value={searchValue} onSearch={setSearchValue} />
          <Routes>
            <Route
              path="/login"
              element={
                user ? (
                  <Navigate to="/login" />
                ) : (
                  <Login onLogin={handleLogin} onLogout={handleLogout} />
                )
              }
            />
            <Route
              path="/profile"
              element={
                user ? (
                  <Profile avatar={user.avatar} userName={user.username} bio={user.bio} />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/PostList"
              element={
                user ? (
                  <PostList searchValue={searchValue} />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
          </Routes>
        </div>
      </PhoneFrame>
    </Router>
  );
}

export default App;
