import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/navBar';
import PostList from './components/postList';
import Profile from './components/profile';
import SearchBar from './components/searchBar'; 
import PostListLayout from './components/postListLayout'; 

function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const [currentView, setCurrentView] = useState('postList');

  const handleSearch = (value) => {
    setSearchValue(value);
  };

  const handleLogoClick = () => {
    setCurrentView('postList');
  };

  const handleProfileClick = () => {
    setCurrentView('profile');
  };

  return (
    <PhoneFrame>
      <div className="container">
        {currentView === 'postList' ? (
          <PostListLayout searchValue={searchValue} onSearch={handleSearch} />
        ) : (
          <div> {/* Render any other content for the "profile" view here */}</div>
        )}
        {currentView === 'postList' ? (
          <PostList searchValue={searchValue} />
        ) : (
          <Profile userName="YourUserName" bio="YourBio" />
        )}
      </div>
    </PhoneFrame>
  );
}

export default App;
