import React, { useState, useEffect } from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import axios from 'axios';

// ... Other imports

function App() {
  const [searchValue, setSearchValue] = useState('');
  const [currentView, setCurrentView] = useState('postList');
  const [authenticated, setAuthenticated] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    // Check if the user is authenticated by checking the token in localStorage
    const token = localStorage.getItem('token');
    if (token) {
      // If a token exists, set authenticated to true
      setAuthenticated(true);

      // Fetch user profile using the token
      axios.get('https://thre-points.herokuapp.com/api/users/profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setCurrentUser(response.data);
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          // If the request returns 401, it means the token is invalid
          // Clear localStorage and reset authentication state
          localStorage.removeItem('token');
          setAuthenticated(false);
        }
      });
    }
  }, []);

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('https://thre-points.herokuapp.com/api/login', {
        username: email,
        password: password,
      });

      if (response.status === 200) {
        const data = response.data;
        localStorage.setItem('token', data.token);
        setAuthenticated(true);
      } else {
        // Handle login error
        // You can set an error state here
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLogout = () => {
    // Clear localStorage and reset authentication state
    localStorage.removeItem('token');
    setAuthenticated(false);
    setCurrentUser(null);
  };

  return (
    <PhoneFrame>
      <div className="container">
        <NavBar
          onLogoClick={() => setCurrentView('postList')}
          onProfileClick={() => setCurrentView('profile')}
          onLogout={handleLogout}
          authenticated={authenticated}
        />
        <SearchBar value={searchValue} onSearch={setSearchValue} />
        <Switch>
          <Route path="/login">
            {authenticated ? (
              <Redirect to="/profile" />
            ) : (
              <Login onLogin={handleLogin} />
            )}
          </Route>
          <Route path="/profile">
            {authenticated ? (
              <Profile avatar={currentUser.avatar} userName={currentUser.username} bio={currentUser.bio} />
            ) : (
              <Redirect to="/login" />
            )}
          </Route>
          <Route path="/postList">
            {authenticated ? (
              <PostList searchValue={searchValue} />
            ) : (
              <Redirect to="/login" />
            )}
          </Route>
        </Switch>
      </div>
    </PhoneFrame>
  );
}

export default App;
