import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/navBar';
import PostList from './components/postList';
import Profile from './components/profile';
import SearchBar from './components/searchBar';
import Login from './components/login';
import axios from 'axios';

function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const [currentView, setCurrentView] = useState('postList');
  const [authenticated, setAuthenticated] = useState(false); // Add authenticated state

  const handleSearch = (value) => {
    setSearchValue(value);
  };

  const handleLogoClick = () => {
    setCurrentView('postList');
  };

  const handleProfileClick = () => {
    setCurrentView('profile');
  };

  return (
    <PhoneFrame>
      <div className="container">
        <NavBar onLogoClick={handleLogoClick} onProfileClick={handleProfileClick} />
        {/* Use the SearchBar component for search input */}
        <SearchBar value={searchValue} onSearch={handleSearch} />
        {currentView === 'postList' && !authenticated ? ( // Use && for conditional rendering
          <PostList searchValue={searchValue} />
        ) : currentView === 'profile' && !authenticated ? ( // Use && for conditional rendering
          <Profile />
        ) : (
          <Login onLogin={() => setAuthenticated(true)} /> // Pass the authenticated state
        )}
      </div>
    </PhoneFrame>
  );
}

export default App;
