import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // Import Navigate
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/NavBar';
import PostList from './components/PostList';
import Profile from './components/Profile';
import SearchBar from './components/SearchBar'; 
import Login from './components/Login'; 

function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const [authenticated, setAuthenticated] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
      // Check if the user is authenticated by checking the token in localStorage
      const token = localStorage.getItem('token');
      if (token) {
        // If a token exists, set authenticated to true
        setAuthenticated(true);

        // Fetch user profile using the token
        axios.get('https://thre-points.herokuapp.com/api/users/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setCurrentUser(response.data);
        })
        .catch((error) => {
          if (error.response && error.response.status === 401) {
            // If the request returns 401, it means the token is invalid
            // Clear localStorage and reset authentication state
            localStorage.removeItem('token');
            setAuthenticated(false);
          }
        });
      }
    }, []);

    const handleLogin = async (email, password) => {
      try {
        const response = await axios.post('https://thre-points.herokuapp.com/api/login', {
          username: email,
          password: password,
        });

        if (response.status === 200) {
          const data = response.data;
          localStorage.setItem('token', data.token);
          setAuthenticated(true);
          navigate('/profile'); // Use navigate to redirect to the profile page
        } else {
          // Handle login error
          // You can set an error state here
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    const handleLogout = () => {
      // Clear localStorage and reset authentication state
      localStorage.removeItem('token');
      setAuthenticated(false);
      setCurrentUser(null);
      navigate('/login'); // Use navigate to redirect to the login page
    };

    return (
      <Router>
        <PhoneFrame>
          <div className="container">
          <NavBar
          onLogoClick={() => navigate('/postList')} // Use navigate for route changes
          onProfileClick={() => navigate('/profile')} // Use navigate for route changes
          onLogout={handleLogout}
          authenticated={authenticated}
        />
        <SearchBar value={searchValue} onSearch={setSearchValue} />
        <Routes>
          <Route path="/login" element={authenticated ? <Navigate to="/profile" /> : <Login onLogin={handleLogin} />} />
          <Route
            path="/profile"
            element={authenticated ? <Profile avatar={currentUser.avatar} userName={currentUser.username} bio={currentUser.bio} /> : <Navigate to="/login" />}
          />
          <Route
            path="/postList"
            element={authenticated ? <PostList searchValue={searchValue} /> : <Navigate to="/login" />}
          />
        </Routes>
        </div>
      </PhoneFrame>
    </Router> 
  );
}

export default App;
