import axios from "axios";

export function login(username, password) {
  return axios
    .post("https://three-points.herokuapp.com/api/login", {
      username,
      password,
    })
    .then((response) => {
      // Assuming the response contains relevant data
      return response.data;
    })
    .catch((error) => {
      // Handle any login errors here, e.g., display an error message
      throw error;
    });
}

export function getPostList() {
  return axios
    .get("https://three-points.herokuapp.com/api/postList", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
    .then((response) => response.data)
    .catch((error) => {
      if (error.response && error.response.status === 401) {
        // Handle 401 (Unauthorized) response here:
        // Clear the token and redirect to the login page
        localStorage.removeItem("token");
        navigate('/login'); // Import useNavigate from 'react-router-dom'
      }
      throw error;
    });
}

export function getProfile() {
  return axios
    .get("https://three-points.herokuapp.com/api/profile", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
    .then((response) => response.data)
    .catch((error) => {
      if (error.response && error.response.status === 401) {
        // Handle 401 (Unauthorized) response here:
        // Clear the token and redirect to the login page
        localStorage.removeItem("token");
        navigate('/login'); // Import useNavigate from 'react-router-dom'
      }
      throw error;
    });
}
