import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/NavBar';
import PostList from './components/PostList';
import Profile from './components/Profile';
import SearchBar from './components/SearchBar';
import Login from './components/Login';
import UserContextProvider, { useUserContext } from './contexts/user-context';
import axios from 'axios'; // Import axios for making API requests


function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const navigate = useNavigate(); // Get the navigate function
  const value = useUserContext(); // Use the user context

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('https://thre-points.herokuapp.com/api/login', {
        username: email,
        password: password,
      });

      if (response.status === 200) {
        const data = response.data;
        localStorage.setItem('token', data.token);
        setAuthenticated(true);
        navigate('/profile'); // Use navigate to redirect to the profile page
      } else {
        // Handle login error
        // You can set an error state here
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLogout = () => {
    // Clear localStorage and reset authentication state
    localStorage.removeItem('token');
    setAuthenticated(false);
    setCurrentUser(null);
    navigate('/login'); // Use navigate to redirect to the login page
  };

  // Use an effect to fetch user data when the component mounts
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // If a token exists in localStorage, fetch user data
      axios
        .get('https://thre-points.herokuapp.com/api/users/profile', {
          headers: {
            Authorization: `Bearer ${token}`, // Use the token from localStorage
          },
        })
        .then((response) => {
          if (response.status === 200) {
            const userData = response.data;
            setUser(userData);
          } else {
            // Handle the case when the request is not successful, e.g., token is invalid
            // Clear localStorage and reset user state
            localStorage.removeItem('token');
            setUser(null);
            navigate('/login'); // Redirect to the login page
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  }, [navigate]);

  if (!user) {
    return (
      <div className="p-5 text-center">
        <div className="spinner-border text-primary" role="status" />
      </div>
    );
  }


  return (
    <UserContextProvider>
      <Router>
        <PhoneFrame>
          <div className="container">
            <NavBar
              onLogoClick={() => navigate('/PostList')}
              onLogout={handleLogout} // Pass the handleLogout function
              authenticated={user !== null} // Check if the user is authenticated
            />
            <SearchBar value={searchValue} onSearch={setSearchValue} />
            <Routes>
              <Route
                path="/login"
                element={
                  user ? (
                    <Navigate to="/login" />
                  ) : (
                    <Login onLogin={handleLogin} onLogout={handleLogout} /> // Pass the functions as props
                  )
                }
              />
              <Route
                path="/profile"
                element={
                  user ? (
                    <Profile avatar={user.avatar} userName={user.username} bio={user.bio} />
                  ) : (
                    <Navigate to="/login" />
                  )
                }
              />
              <Route
                path="/PostList"
                element={
                  user ? (
                    <PostList searchValue={searchValue} />
                  ) : (
                    <Navigate to="/login" />
                  )
                }
              />
            </Routes>
          </div>
        </PhoneFrame>
      </Router>
    </UserContextProvider>
  );
}

export default App;
