import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/navBar';
import PostList from './components/postList';
import Profile from './components/profile';
import SearchBar from './components/searchBar';
import Login from './components/login';
import { Route, Switch, useHistory } from 'react-router-dom';

function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const [currentView, setCurrentView] = useState('postList');
  const [token, setToken] = useState(localStorage.getItem('token'));
  const history = useHistory();

  const handleSearch = (value) => {
    setSearchValue(value);
  };

  const handleLogoClick = () => {
    setCurrentView('postList');
    history.push('/postList'); // Navigate to the PostList page
  };

  const handleProfileClick = () => {
    setCurrentView('profile');
    history.push('/profile'); // Navigate to the Profile page
  };

  return (
    <PhoneFrame>
      <div className="container">
        {token ? (
          <>
            {/* Render NavBar with Profile link if not on Login page */}
            {history.location.pathname !== '/' && (
              <NavBar
                onLogoClick={handleLogoClick}
                onProfileClick={handleProfileClick}
                showProfileLink={true}
              />
            )}
            <SearchBar value={searchValue} onSearch={handleSearch} />
            <Switch>
              <Route path="/postList">
                <PostList searchValue={searchValue} />
              </Route>
              <Route path="/profile">
                <Profile />
              </Route>
            </Switch>
          </>
        ) : (
          <Login setToken={setToken} />
        )}
      </div>
    </PhoneFrame>
  );
}

export default App;
