import React, { useState } from 'react';
import { login } from '../service/dataservice'; // Import your login function

function Login({ setAuthenticated }) {
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const form = event.target;

    const username = form.username.value;
    const password = form.password.value;

    try {
      // Send a POST request to the login API with user credentials
      const response = await login(username, password);

      if (response && response.status === 200) {
        // If the login is successful, save the token to localStorage and set authenticated to true
        const token = response.data.token;
        localStorage.setItem('token', token);
        setAuthenticated(true);
      } else {
        // If the login fails, display an error message
        setError('Login failed. Please check your credentials.');
      }
    } catch (err) {
      // Handle any errors that occur during the request
      setError('Login failed. Please check your credentials.');
    }
  };

  return (
    <div>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          className="form-control mb-3"
          placeholder="Username"
          required
        />
        <input
          type="password"
          name="password"
          className="form-control mb-3"
          placeholder="Password"
          required
        />
        <button type="submit" className="btn btn-success">
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
