import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/NavBar';
import PostList from './components/PostList';
import Profile from './components/Profile';
import SearchBar from './components/SearchBar';
import Login from './components/Login';
import UserContextProvider, { useUserContext } from './contexts/user-context';

function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const { user, setAuthenticated, setCurrentUser } = useUserContext(); // Use the user context
  const navigate = useNavigate(); // Get the navigate function

  useEffect(() => {
    // Check if the user is authenticated by checking the user context
    if (user) {
      // Fetch user profile using the user data
      axios.get('https://thre-points.herokuapp.com/api/users/profile', {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      })
      .then((response) => {
        // Update the user context with the user profile data if needed
        // You can use setUserProfileData or any other function to update the user context
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          // If the request returns 401, it means the token is invalid
          // Clear localStorage and reset authentication state if needed
          localStorage.removeItem('token');
          setAuthenticated(false);
          setCurrentUser(null);
          navigate('/login'); // Redirect to the login page
        }
      });
    }
  }, [user, setAuthenticated, setCurrentUser, navigate]);

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('https://thre-points.herokuapp.com/api/login', {
        username: email,
        password: password,
      });

      if (response.status === 200) {
        const data = response.data;
        localStorage.setItem('token', data.token);
        setAuthenticated(true);
        navigate('/profile'); // Use navigate to redirect to the profile page
      } else {
        // Handle login error
        // You can set an error state here
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLogout = () => {
    // Clear localStorage and reset authentication state
    localStorage.removeItem('token');
    setAuthenticated(false);
    setCurrentUser(null);
    navigate('/login'); // Use navigate to redirect to the login page
  };

  return (
    <UserContextProvider>
      <Router>
        <PhoneFrame>
          <div className="container">
            <NavBar
              onLogoClick={() => navigate('/PostList')}
              onLogout={handleLogout} // Pass the handleLogout function
              authenticated={user !== null} // Check if the user is authenticated
            />
            <SearchBar value={searchValue} onSearch={setSearchValue} />
            <Routes>
              <Route
                path="/login"
                element={
                  user ? (
                    <Navigate to="/profile" />
                  ) : (
                    <Login onLogin={handleLogin} /> // Pass the handleLogin function
                  )
                }
              />
              <Route
                path="/profile"
                element={
                  user ? (
                    <Profile avatar={user.avatar} userName={user.username} bio={user.bio} />
                  ) : (
                    <Navigate to="/login" />
                  )
                }
              />
              <Route
                path="/PostList"
                element={
                  user ? (
                    <PostList searchValue={searchValue} />
                  ) : (
                    <Navigate to="/login" />
                  )
                }
              />
            </Routes>
          </div>
        </PhoneFrame>
      </Router>
    </UserContextProvider>
  );
}

export default App;
