import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/NavBar';
import PostList from './components/PostList';
import Profile from './components/Profile';
import SearchBar from './components/SearchBar';
import Login from './components/Login';
import UserContextProvider, { useUserContext } from './contexts/user-context';

function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const { user } = useUserContext(); // Use the user context

  useEffect(() => {
    // Check if the user is authenticated by checking the user context
    if (user) {
      // Fetch user profile using the user data
      axios.get('https://thre-points.herokuapp.com/api/users/profile', {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      })
      .then((response) => {
        // Update the user context with the user profile data if needed
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          // If the request returns 401, it means the token is invalid
          // Clear localStorage and reset authentication state if needed
        }
      });
    }
  }, [user]);

  // Other authentication and login/logout code can remain the same

