import React, { useState } from 'react';
import axios from 'axios'; // Import Axios
import { login } from '../service/dataservice'; // Import your dataservice functions

function Login({ onLogin, setAuthenticated }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(false);

  const handleLogin = async () => {
    try {
      // Call the login function from the dataservice module
      const data = await login(email, password);

      if (data && data.token) {
        localStorage.setItem('token', data.token);
        setAuthenticated(true); // Update the authenticated state
        onLogin();
      } else {
        setError(true);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Login</h2>
        {error && (
          <button className="error-button">
            Invalid email or password
          </button>
        )}
        <div className="input-container">
          <label htmlFor="email">Email</label>
          <input
            type="text"
            id="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input-field"
          />
        </div>
        <div className="input-container">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="input-field"
          />
        </div>
        <button className="login-button" onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
}

export default Login;
