import React, { useState, useEffect } from 'react'; // Import useEffect
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/NavBar';
import PostList from './components/PostList';
import Profile from './components/Profile';
import SearchBar from './components/SearchBar';
import Login from './components/Login';
import axios from 'axios';
import { useUserContext, UserContextProvider } from './contexts/user-context'; // Import UserContextProvider and useUserContext


function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const navigate = useNavigate();


  return (
    <Router>
      <PhoneFrame>
        <div className="container">
          <NavBar
            onLogoClick={() => navigate('/PostList')}
            onLogout={handleLogout}
            authenticated={user !== null}
          />
          <SearchBar value={searchValue} onSearch={setSearchValue} />
          <Routes>
            <Route
              path="/login"
              element={
                user ? (
                  <Navigate to="/login" />
                ) : (
                  <Login onLogin={handleLogin} onLogout={handleLogout} />
                )
              }
            />
            <Route
              path="/profile"
              element={
                user ? (
                  <Profile avatar={user.avatar} userName={user.username} bio={user.bio} />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/PostList"
              element={
                user ? (
                  <PostList searchValue={searchValue} />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
          </Routes>
        </div>
      </PhoneFrame>
    </Router>
  );
}

export default App;
