import axios from "axios";

// Function to send a POST request for login
export function login(username, password) {
  return axios
    .post("https://three-points.herokuapp.com/api/login", {
      username,
      password,
    })
    .then((response) => {
      // Assuming the response contains relevant data
      return response.data;
    })
    .catch((error) => {
      // Handle any login errors here, e.g., display an error message
      throw error;
    });
}

export function getPostList() {
  return axios
    .get("https://three-points.herokuapp.com/api/postList", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
    .then((response) => response.data);
}

export function getProfile() {
  return axios
    .get("https://three-points.herokuapp.com/api/profile", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
    .then((response) => response.data);
}
