import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import NavBar from './components/NavBar';
import PostList from './components/PostList';
import Profile from './components/Profile';
import SearchBar from './components/SearchBar';
import Login from './components/Login';
import UserContextProvider, { useUserContext } from './contexts/user-context';

function PhoneFrame({ children }) {
  return (
    <div className="phone-frame">
      <div className="content">{children}</div>
    </div>
  );
}

function App() {
  const [searchValue, setSearchValue] = useState('');
  const { user } = useUserContext(); // Use the user context

  useEffect(() => {
    // Check if the user is authenticated by checking the user context
    if (user) {
      // Fetch user profile using the user data
      axios.get('https://thre-points.herokuapp.com/api/users/profile', {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      })
      .then((response) => {
        // Update the user context with the user profile data if needed
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          // If the request returns 401, it means the token is invalid
          // Clear localStorage and reset authentication state if needed
        }
      });
    }
  }, [user]);

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('https://thre-points.herokuapp.com/api/login', {
        username: email,
        password: password,
      });

      if (response.status === 200) {
        const data = response.data;
        localStorage.setItem('token', data.token);
        setAuthenticated(true);
        navigate('/profile'); // Use navigate to redirect to the profile page
      } else {
        // Handle login error
        // You can set an error state here
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLogout = () => {
    // Clear localStorage and reset authentication state
    localStorage.removeItem('token');
    setAuthenticated(false);
    setCurrentUser(null);
    navigate('/login'); // Use navigate to redirect to the login page
  };

  return (
    <UserContextProvider> {/* Wrap the entire app with UserContextProvider */}
      <Router>
        <PhoneFrame>
          <div className="container">
            <NavBar
              onLogoClick={() => navigate('/PostList')}
              onLogout={handleLogout}
              authenticated={user !== null} // Check if user is authenticated based on context
            />
            <SearchBar value={searchValue} onSearch={setSearchValue} />
            <Routes>
              <Route
                path="/Login"
                element={
                  user ? (
                    <Navigate to="/Profile" />
                  ) : (
                    <Login onLogin={handleLogin} />
                  )
                }
              />
              <Route
                path="/Profile"
                element={
                  user ? (
                    <Profile avatar={user.avatar} userName={user.username} bio={user.bio} />
                  ) : (
                    <Navigate to="/Login" />
                  )
                }
              />
              <Route
                path="/PostList"
                element={
                  user ? (
                    <PostList searchValue={searchValue} />
                  ) : (
                    <Navigate to="/Login" />
                  )
                }
              />
            </Routes>
          </div>
        </PhoneFrame>
      </Router>
    </UserContextProvider>
  );
}

export default App;
